class bad_words(feature_extraction):
    def run(self,D,columns):
        def custom_function(x):
            global all_counter
            target = ""
            for column in columns:
                target += "{} ".format(x[column])
            target = word_tokenize(target)
            counter = 0
            for word in banned_words:
                if word in target:
                    counter += 1

            return counter

        D["number_of_bad_words"] = D.apply(custom_function, axis=1)